CREATE TABLE IF NOT EXISTS projects (id INTEGER PRIMARY KEY AUTOINCREMENT, name TEXT UNIQUE NOT NULL, path TEXT NOT NULL, description TEXT, project_type TEXT, created_at DATETIME DEFAULT CURRENT_TIMESTAMP, last_accessed DATETIME DEFAULT CURRENT_TIMESTAMP);
CREATE TABLE IF NOT EXISTS agent_executions (id INTEGER PRIMARY KEY AUTOINCREMENT, agent_name TEXT NOT NULL, task_description TEXT NOT NULL, execution_time REAL, success BOOLEAN NOT NULL, timestamp DATETIME DEFAULT CURRENT_TIMESTAMP);
CREATE TABLE IF NOT EXISTS agent_mistakes (id INTEGER PRIMARY KEY AUTOINCREMENT, agent_name TEXT NOT NULL, mistake_type TEXT NOT NULL, context TEXT, correction TEXT, timestamp DATETIME DEFAULT CURRENT_TIMESTAMP);
CREATE TABLE IF NOT EXISTS agent_knowledge_base (id INTEGER PRIMARY KEY AUTOINCREMENT, category TEXT NOT NULL, pattern_name TEXT NOT NULL, description TEXT, code_example TEXT, success_rate REAL DEFAULT 0.0, usage_count INTEGER DEFAULT 0, created_at DATETIME DEFAULT CURRENT_TIMESTAMP);
